        const approvedAdvances = monthlyAdvances.filter((req) => req.status === "approved").reduce((sum3, req) => sum3 + parseFloat(req.paidAmount || "0"), 0);
        const pendingAdvances = monthlyAdvances.filter((req) => req.status === "pending").reduce((sum3, req) => sum3 + parseFloat(req.requestAmount), 0);
        const remainingAdvanceBalance = monthlyAdvances.filter((req) => req.status === "approved" && parseFloat(req.remainingBalance || "0") > 0).reduce((sum3, req) => sum3 + parseFloat(req.remainingBalance || "0"), 0);
        const overtimeLogs = await this.getStaffOvertimeLogs(organizationId2, { staffId });
        const monthlyOvertimes = overtimeLogs.filter((log2) => {
          const workDate = new Date(log2.workDate);
          return workDate.getFullYear() === year && workDate.getMonth() + 1 === month;
        });
        const totalOvertimeHours = monthlyOvertimes.reduce((sum3, log2) => sum3 + parseFloat(log2.totalHours), 0);
        const approvedOvertimeHours = monthlyOvertimes.filter((log2) => log2.status === "approved").reduce((sum3, log2) => sum3 + parseFloat(log2.totalHours), 0);
        const pendingOvertimeHours = monthlyOvertimes.filter((log2) => log2.status === "pending").reduce((sum3, log2) => sum3 + parseFloat(log2.totalHours), 0);
        const overtimeEarnings = monthlyOvertimes.filter((log2) => log2.status === "approved" && log2.compensationType === "overtime_pay").reduce((sum3, log2) => sum3 + parseFloat(log2.compensationAmount || "0"), 0);
        const timeOffCredits = monthlyOvertimes.filter((log2) => log2.status === "approved" && log2.compensationType === "time_off_credit").reduce((sum3, log2) => sum3 + parseFloat(log2.timeOffHours || "0"), 0);
        const emergencyTasks = monthlyOvertimes.filter((log2) => log2.isEmergency).length;
        const afterHoursTasks = monthlyOvertimes.length;
        const summaryData = {
          organizationId: organizationId2,
          staffId,
          summaryYear: year,
          summaryMonth: month,
          summaryPeriod,
          totalAdvanceRequests,
          approvedAdvances: approvedAdvances.toString(),
          pendingAdvances: pendingAdvances.toString(),
          remainingAdvanceBalance: remainingAdvanceBalance.toString(),
          totalOvertimeHours: totalOvertimeHours.toString(),
          approvedOvertimeHours: approvedOvertimeHours.toString(),
          pendingOvertimeHours: pendingOvertimeHours.toString(),
          overtimeEarnings: overtimeEarnings.toString(),
          timeOffCredits: timeOffCredits.toString(),
          emergencyTasks,
          afterHoursTasks
        };
        return await this.createOrUpdateStaffMonthlySummary(summaryData);
